//SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;
import '../interfaces/IRahatClaim.sol';
import '@openzeppelin/contracts/utils/Multicall.sol';

contract RahatClaim is IRahatClaim, Multicall {
  event ClaimCreated(
    uint256 indexed claimId,
    address indexed claimer,
    address indexed claimee,
    address token,
    address otpServer,
    uint256 amount
  );
  event OtpAddedToClaim(uint256 indexed claimId);
  event ClaimProcessed(uint256 indexed claimId);

  mapping(uint256 => Claim) public claims;
  uint256 public claimCount;

  function createClaim(
    address _claimerAddress,
    address _claimeeAddress,
    address _otpServerAddress,
    address _tokenAddress,
    uint256 _amount
  ) public returns (uint256 claimId) {
    claimId = ++claimCount;
    claims[claimId] = Claim({
      ownerAddress: msg.sender,
      claimerAddress: _claimerAddress,
      claimeeAddress: _claimeeAddress,
      otpServerAddress: _otpServerAddress,
      tokenAddress: _tokenAddress,
      amount: _amount,
      expiryDate: 0,
      otpHash: bytes32(0),
      isProcessed: false
    });
    emit ClaimCreated(
      claimId,
      _claimerAddress,
      _claimeeAddress,
      _tokenAddress,
      _otpServerAddress,
      _amount
    );
  }

  function addOtpToClaim(uint256 _claimId, bytes32 _otpHash, uint256 _expiryDate) public {
    Claim storage _claim = claims[_claimId];
    require(_claim.otpServerAddress == msg.sender, 'unauthorized otpServer');
    require(_claim.isProcessed == false, 'already processed');
    _claim.expiryDate = _expiryDate;
    _claim.otpHash = _otpHash;
    emit OtpAddedToClaim(_claimId);
  }

  function processClaim(uint256 _claimId, string memory _otp) public returns (Claim memory claim_) {
    Claim storage _claim = claims[_claimId];
    require(_claim.ownerAddress == msg.sender, 'not owner');
    require(block.timestamp <= _claim.expiryDate, 'expired');
    require(_claim.isProcessed == false, 'already processed');
    bytes32 _otpHash = findHash(_otp);
    require(_claim.otpHash == _otpHash, 'invalid otp');

    _claim.isProcessed = true;
    emit ClaimProcessed(_claimId);
    return _claim;
  }

  function findHash(string memory _data) public pure returns (bytes32) {
    return keccak256(abi.encodePacked(_data));
  }
}

src/contracts/RahatPayrollProject.sol.content:
//SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '@openzeppelin/contracts/metatx/ERC2771Context.sol';
import '@openzeppelin/contracts/metatx/ERC2771Forwarder.sol';
import '@openzeppelin/contracts/metatx/ERC2771Forwarder.sol';
import '../interfaces/IRahatPayrollProject.sol';
import '../libraries/AbstractProject.sol';
import '../interfaces/IRahatClaim.sol';
import './disbursementTriggers/MatchingFund.sol';

// TODO:
// - allocate tokens to beneficiaries
// - get list of beneficiaries
// - approvedClaimers
// - updateClaimer
// - updateDisbursmentTriggerContract
// - disburseTokensBatch
// - disburseTokenByBeneficiary / claim beneficairy token
// - claim beneficairy token
contract RahatPayrollProject is AbstractProject {
  event ClaimerUpdated(address indexed claimer, bool approved);
  event ClaimProcessed(
    address indexed beneficiary,
    address indexed claimer,
    address indexed token,
    uint256 amount
  );

  bytes4 public constant IID_RAHAT_PROJECT = type(IRahatProject).interfaceId;

  constructor(
    string memory _name,
    address _rahatClaim,
    address _forwarder
  ) AbstractProject(_name, _forwarder) {}

  function updateClaimer(address _claimer, bool _approved) external {
    //TODO: Implement claimer approval logic
    emit ClaimerUpdated(_claimer, _approved);
  }

  function disburseTokenBatch(address _tokenAddress) external {
    //TODO: Implement disbursement logic
    //check for disbursement trigger
    require(IERC20(_tokenAddress).balanceOf(address(this)) >= 0, 'no balance');
  }

  //Only Claimers can call this function

  //disburseTokenByBeneficiary
  function transferTokenToClaimer(
    address _tokenAddress,
    address _benAddress,
    address _vendorAddress,
    uint _amount
  ) public {
    require(
      tokenAllocations[_tokenAddress][_benAddress] >= _amount,
      'not enough balace'
    );
    tokenAllocations[_tokenAddress][_benAddress] -= _amount;
    require(
      IERC20(_tokenAddress).transfer(_vendorAddress, _amount),
      'transfer failed'
    );
    emit ClaimProcessed(_benAddress, _vendorAddress, _tokenAddress, _amount);
  }

  function supportsInterface(
    bytes4 interfaceId
  ) public view virtual override returns (bool) {
    return interfaceId == IID_RAHAT_PROJECT;
  }
}

